import styled from "styled-components";

export const Container = styled.header`
  background: #fff;
  max-width: 62.5rem;
  margin: 0 auto;
  /* box-shadow: 0px 0px 10px 0px rgba(0,0,0,0.3); */
`
export const Content = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  max-width: 980px ;
  margin: 0 auto;
  padding: 1rem;

  h1{
    font-size: 3rem;
    font-weight: 300;
  }
`
