import { Container } from "./styles";
import { api } from "../../services/api";
import { useEffect, useState } from "react";

interface album {
  id: number;
  name: string;
  tracks: object;
  year: number;
}

export function Listas(){
  const [album, setAlbum] = useState<album[]>([]);

  useEffect(() => {
    api.get('album')
    .then(response => console.log(response.data.data.tracks)) //setAlbum(response.data.data))
  }, []);

  return(
    <Container>
      <table>
        <thead>

          {album.map(album => (
            <tr key={album.id}>
            <th>Álbum:</th>
            <th>{album.name}, {album.year}</th>
          </tr>
          ))}
          
        </thead>
        <tbody>
        <tr>
            <td>Nº</td>
            <td>Faixa</td>
            <td>Duração</td>
          </tr>
          
          <tr>
            <td>11</td>
            <td>Minas Gerais</td>
            <td>3:47</td>
          </tr>
        </tbody>
      </table>
      
    </Container>
  )
}
