import styled from "styled-components";

export const Container = styled.section`
  color: rgb(98, 98, 98);
  max-width: 980px;

    fieldset{
  display: flex;
  align-items: center;
}

  input {
  border: 2px solid red;
  border-radius: 30px;
  height: 3rem;
  flex: 1;
  margin: 5px 5px 5px 0;
  padding: 1.5rem;
}

    button {
  border: none;
  height: 3rem;
  width: 20%;
  border-radius: 30px;
  background-color: #0A95FF;
  color: #fff;
  cursor: pointer;
  margin-left: 4px;
}

    button:hover {
  background-color: #4ab1ff;
}
`
