import styled from "styled-components";

export const Container = styled.div`
   color: rgb(98, 98, 98);
    max-width: 980px;
    margin: 0 auto;

  input {
  
  border-radius: 30px;
  flex: 1;
  margin: 
  padding: 1.5rem;
}

    button {
  border: none;
  height: 3rem;
  width: 20%;
  border-radius: 30px;
  background-color: #0A95FF;
  color: #fff;
  cursor: pointer;
  margin-left: 4px;
}

    button:hover {
  background-color: #4ab1ff;
}
`
