import styled from "styled-components";

export const Container = styled.div`
   color: rgb(98, 98, 98);
    max-width: 980px;
    margin: 0 auto;

    input {
    border: none;
    border-radius: 10px;
    padding: 10px;
    width: 50%;
}

    button {
  border: none;
  height: 3rem;
  width: 20%;
  border-radius: 30px;
  background-color: #0A95FF;
  color: #fff;
  cursor: pointer;
  margin-left: 4px;
}

    button:hover {
  background-color: #4ab1ff;
}
    `

    export const Content = styled.form`
        background: red;
        max-width: 100%;
      `
