import { Container } from "./styles";
import { api } from "../../services/api";
import { useEffect, useState } from "react";

interface album {
  id: number;
  name: string;
  tracks: object;
  number: number;
  title: string;
  year: number;
}

interface tracks {
  id: number;
  number: number;
  title: string;
  duration: number;
}


export function Listas(){
  const [album, setAlbum] = useState<album[]>([]);
  const [tracks, setTracks] = useState<tracks[]>([]);

  useEffect(() => {
    api.get('album')
    .then(response => setAlbum(response.data.data) //setAlbum(response.data.data))
  )}, []);

  return(
    <Container>
      <table>
        <thead>

          {album.map(item => (
            <tr key={item.id}>
            <th>Álbum:</th>
            <th>{item.name}, {item.year}</th>
          </tr>
          ))}
          
        </thead>
        <tbody>
        <tr>
          <td>Nº</td>
          <td>Faixa</td>
          <td>Duração</td>
        </tr>
        </tbody>
        
          {tracks.map(item => {
            return(
          <tr key={item.id}>
            <td>{item.number}</td>
            <td>{item.title}</td>
            <td>{item.duration}</td>
          </tr>
            )
          })}
          
          
          
          
      </table>
     
      
    </Container>
  )
}
