import styled from "styled-components"

export const Container = styled.div`
  margin-top: 2rem;

  table{
    width: 100%;
    border-spacing: 0 0.5rem;
  }

  th{
    font-weight: 600;
    text-align: left;
    line-height: 1.5rem;
  }

  td{
    padding: .125rem;
  }
`
